                    $.ajax(""/Games/Sudoku"", {
                        type: 'post',
                        data: json,
                        contentType: ""application/json; charset=utf8""
                    }).then(function (arr) {
                        if (arr[0] == true) {
                            $('#main').append('<h1>Win</h1>');
                            let clockId = localStorage.getItem(""clockId"");
                            clearInterval(clockId);
                        }
                        if (arr[1] == false) {
